import React, { Component } from "react";
import CustomBreadcrumb from "../../../components/CustomBreadcrumb";
import {
  Alert,
  Layout,
  Row,
  Col,
  Divider,
  Form,
  Button,
  Icon,
  Input,
  InputNumber,
  Checkbox,
  Tooltip,
  Cascader,
  Select,
  DatePicker,
  Radio,
  Rate,
  Switch,
  Slider,
  AutoComplete,
  message
} from "antd";
import "../../../assets/styles/view-style/form.scss";

const { Option } = Select;
const AutoCompleteOption = AutoComplete.Option;

const residences = [
  {
    value: "sichuan",
    label: "四川",
    children: [
      {
        value: "chengdu",
        label: "成都",
        children: [
          {
            value: "gaoxin",
            label: "高新区"
          }
        ]
      }
    ]
  },
  {
    value: "gansu",
    label: "甘肃",
    children: [
      {
        value: "lanzhou",
        label: "兰州",
        children: [
          {
            value: "anning",
            label: "安宁区"
          }
        ]
      }
    ]
  }
];

class FromView extends Component {
  state = {
    confirmDirty: false,
    autoCompleteResult: [],
    visible: true
  };

  handleClose = () => {
    this.setState({ visible: false });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, fieldsValue) => {
      if (err) return;
      const values = {
        ...fieldsValue,
        "date-picker": fieldsValue["date-picker"]
          ? fieldsValue["date-picker"].format("YYYY-MM-DD")
          : ""
      };
      console.log("这就是你填好的数据" + values);
      message.info("你很棒哦,这么快就填好了!");
    });
  };

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue("password")) {
      callback("两次输入密码不一致!");
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(["confirm"], { force: true });
    }
    callback();
  };

  handleWebsiteChange = value => {
    let autoCompleteResult;
    if (!value) {
      autoCompleteResult = [];
    } else {
      autoCompleteResult = ["@google.com", "@163.com", "@qq.com"].map(
        domain => `${value}${domain}`
      );
    }
    this.setState({ autoCompleteResult });
  };

  render() {
    const { getFieldDecorator, getFieldValue } = this.props.form;

    const formItemLayout = {
      labelCol: {
        xs: { span: 16 },
        sm: { span: 6 }
