      <AutoCompleteOption key={website}>{website}</AutoCompleteOption>
    ));

    return (
      <Layout className="animated fadeIn">
        <div>
          <CustomBreadcrumb arr={["表单", "基础表单"]}></CustomBreadcrumb>
        </div>
        <div className="base-style">
          <h3>何时使用</h3>
          <Divider></Divider>
          <p>用于创建一个实体或收集信息。</p>
          <p>需要对输入的数据类型进行校验时。</p>
        </div>

        <Row>
          <Col>
            <div className="base-style">
              <div>
                {this.state.visible ? (
