                      用户名&nbsp;
                      <Tooltip title="可以尽量好听点，真的!">
                        <Icon type="question-circle-o" />
                      </Tooltip>
                    </span>
                  }
                >
                  {getFieldDecorator("username", {
                    rules: [{ required: true, message: "请输入用户名" }]
                  })(<Input placeholder="请输入用户名" />)}
                </Form.Item>
                <Form.Item label="性别">
                  {getFieldDecorator("sex", {
                    rules: [{ required: true, message: "请选择性别" }]
                  })(
                    <Radio.Group>
                      <Radio value="man">男</Radio>
                      <Radio value="women">女</Radio>
                      <Radio value="unknow">不详</Radio>
                    </Radio.Group>
                  )}
                </Form.Item>
                <Form.Item label="爱好">
                  {getFieldDecorator("hobby", {
                    rules: [{ required: true, message: "请至少选择一个爱好" }],
                    initialValue: ["A", "B"]
                  })(
                    <Checkbox.Group style={{ width: "100%" }}>
                      <Row>
                        <Col span={8}>
                          <Checkbox value="A">A</Checkbox>
                        </Col>
                        <Col span={8}>
                          <Checkbox disabled value="B">
                            B
                          </Checkbox>
                        </Col>
                        <Col span={8}>
                          <Checkbox value="C">C</Checkbox>
                        </Col>
                      </Row>
                    </Checkbox.Group>
                  )}
                </Form.Item>
                <Form.Item label="年龄">
                  {getFieldDecorator("age", {
                    rules: [{ required: true, message: "请输入年龄" }]
                  })(
                    <InputNumber
                      placeholder="请输入年龄"
                      style={{ width: "100%" }}
                    />
                  )}
                </Form.Item>
                <Form.Item label="出生年月">
                  {getFieldDecorator("date-picker", {
                    rules: [
                      { type: "object", required: true, message: "请选择日期" }
                    ]
                  })(
                    <DatePicker
                      style={{ width: "100%" }}
                      placeholder="请选择日期"
                    />
                  )}
                </Form.Item>

                <Form.Item label="邮箱">
                  {getFieldDecorator("email", {
                    rules: [
                      {
                        type: "email",
                        message: "请输入正确的邮箱!"
                      },
                      {
                        required: true,
                        message: "请输入邮箱"
                      }
                    ]
                  })(
                    <AutoComplete
                      dataSource={websiteOptions}
                      onChange={this.handleWebsiteChange}
                      placeholder="请输入邮箱"
                    >
                      <Input />
                    </AutoComplete>
                  )}
                </Form.Item>
                <Form.Item label="密码" hasFeedback>
                  {getFieldDecorator("password", {
                    rules: [
                      {
                        required: true,
                        message: "请输入密码!"
                      },
                      {
                        validator: this.validateToNextPassword
                      }
                    ]
                  })(<Input.Password placeholder="请输入密码" />)}
                </Form.Item>
                <Form.Item label="确认密码" hasFeedback>
                  {getFieldDecorator("confirm", {
                    rules: [
                      {
                        required: true,
                        message: "请确认密码!"
                      },
                      {
                        validator: this.compareToFirstPassword
                      }
                    ]
                  })(
                    <Input.Password
                      onBlur={this.handleConfirmBlur}
                      placeholder="请确认密码"
                    />
                  )}
                </Form.Item>
                <Form.Item label="家庭住址">
                  {getFieldDecorator("adress", {
                    initialValue: ["sichuan", "chengdu", "gaoxin"],
                    rules: [
                      { type: "array", required: true, message: "请选择住址!" }
                    ]
                  })(
                    <Cascader options={residences} placeholder="请选择住址" />
                  )}
                </Form.Item>
                <Form.Item label="联系电话" extra="你最好写真实的电话号码.">
                  {getFieldDecorator("phone", {
                    rules: [{ required: true, message: "请输入联系电话!" }]
                  })(<Input addonBefore={prefixSelector} />)}
                </Form.Item>
                <Form.Item label="评分" extra="这个项目怎么样.">
                  {getFieldDecorator("rate", {
                    initialValue: 5
                  })(<Rate disabled allowHalf />)}
                </Form.Item>
                <Form.Item label="switch">
                  {getFieldDecorator("switch", {
                    valuePropName: "checked",
                    initialValue: true
                  })(<Switch />)}
                </Form.Item>
                <Form.Item label="slider">
